import {browser} from './definitions/Browser';
import StreamTypes from './definitions/StreamTypes';
import * as Events from './definitions/Events';
import {audio, video} from './definitions/Codec';
import User from './core/User';
import Room from './core/Room';
import Invite from './core/Invite';
import * as DataSync from './core/util/DataSync';
import cache from './core/util/cache';
import * as Log from './core/util/Log';
import Media from './core/util/Media';
import Webcom from 'webcom/webcom';

/**
 * Entry point for Reach SDK
 * @public
 */
export default class Reach {
	/**
	 * Create Reach's data structure where the url points to (might not be the root of your namespace)
	 * @public
	 * @param {string|Webcom} url The url of your namespace or an existing Webcom reference.
	 * @param {Config} [cfg] Reach configuration. You can pass constraints here
	 * @example <caption>Init with the default configuration</caption>
	 * var myReach = new Reach('https://io.datasync.orange.com/base/<my_namespace>');
	 * @example <caption>Init and set constraints for SD video and logLevel to 'info'</caption>
	 * var myReach = new Reach('https://io.datasync.orange.com/base/<my_namespace>', {
	 *  constraints: Reach.media.constraints('SD'),
	 *  logLevel: 'INFO'
	 * });
	 */
	constructor(url, cfg = null) {
		// Set shared reference
		cache.base = url;
		// Set shared configuration
		cache.config = cfg;
		/**
		 * List of declared callbacks
		 * @type {Object}
		 */
		this._callbacks = {};
	}

	/**
	 * Get versions of SDK and DataModel.The Schema version can be used to determine compatibility with the Android & iOS SDK.
	 * @return {{sdk: string, schema: string}}
	 */
	static get version() {
		return {sdk: SDK_VERSION, schema: SCHEMA_VERSION};
	}

	/**
	 * The supported stream types
	 * @returns {StreamTypes}
	 */
	static get types() {
		return StreamTypes;
	}

	/**
	 * The supported events
	 * @return {{room: Events/Room, reach: Events/Reach, stream: Events/Stream}}
	 */
	static get events() {
		return {room: Events.room, reach: Events.reach, stream: Events.stream, invite: Events.invite};
	}

	/**
	 * The browser's details
	 * @return {Browser}
	 */
	static get browser() {
		return browser;
	}

	/**
	 * Media utility functions
	 * @return {Media}
	 */
	static get media() {
		return Media;
	}

	/**
	 * The codec presets to use when setting {@link Config#preferredAudioCodec} or {@link Config#preferredVideoCodec}
	 * @return {{audio: Codec/audio, video: Codec/video}}
	 */
	static get codecs() {
		return {audio, video};
	}
	/**
	 * DataSync reference
	 * @type {Webcom}
	 */
	get base() {
		return cache.base;
	}

	/**
	 * The configuration
	 * @type {Config}
	 */
	get config() {
		return cache.config;
	}

	/**
	 * The connected User
	 * @type {User}
	 */
	get current() {
		return cache.user;
	}

	/**
	 * Register & Sign-in as a new user
	 * @param {string} email The email of the user
	 * @param {string} password The password of the user
	 * @param {string} [name] The display name of the user (defaults to email)
	 * @param {boolean} [rememberMe=false] keep user connected ?
	 * @returns {Promise<User>}
	 */
	register(email, password, name, rememberMe) {
		return cache.base.createUser(email, password)
			.then(auth => {
				if(auth) {
					return this.login(email, password, name || email, rememberMe);
				}
			})
			.catch(Log.r('Reach~register'));
	}

	/**
	 * Sign-in an existing user
	 * @param {string} email The email of the user
	 * @param {string} password The password of the user
 	 * @param {string} [name] The name of the user. Defaults to the value in base.
	 * @param {boolean} [rememberMe=false] keep user connected ?
	 * @returns {Promise<User>}
	 */
	login(email, password, name, rememberMe = false) {
		// Force logout to bypass Webcom bug
		let p = Promise.resolve();
		if(this.current && this.current.email !== email) {
			p = this.logout();
		}
		return p
			.then(() => cache.base.authWithPassword({email, password, rememberMe}))
			.then(auth => User.init(auth, name))
			.then(u => {
				cache.user = u;
				return u;
			})
			.catch(Log.r('Reach~login'));
	}

	/**
	 * Resume previous session
	 * @returns {Promise<User>}
	 */
	resume() {
		return new Promise((resolve, reject) => {
			// Resume session
			if(Webcom.INTERNAL.PersistentStorage.get('session')){
				cache.base.resume((error, auth) => {
					if(auth && !this.current) {
						User.init(auth).then(u => {
							cache.user = u;
							resolve(u);
						}, reject);
					}
				});
			} else {
				reject(new Error('No session to resume'));
			}
		});
	}

	/**
	 * Sign-in an anonymous user
	 * @param {string} name The display name of the user
	 * @returns {Promise<User>}
	 */
	anonymous(name) {
		// Force logout to bypass Webcom bug
		let p = Promise.resolve();
		if(this.current && (!this.current.anonymous || this.current.name !== name)) {
			p = this.logout();
		}
		return p
			.then(() => cache.base.authAnonymously())
			.then(auth => User.init(auth, name))
			.then(u => {
				cache.user = u;
				return u;
			})
			.catch(Log.r('Reach~anonymous'));
	}

	/**
	 * Logout current user
	 * @returns {Promise}
	 */
	logout() {
		return new Promise((resolve, reject) => {
			let p = Promise.resolve();
			if(this.current != null) {
				p = User.disconnect(this.current);
			}
			p.then(() => {
				Object.keys(this._callbacks).forEach(
					event => DataSync.off(Events.reach.toPath(event)(cache.user), event)
				);
				cache.base.logout(() => {
					cache.user = null;
					resolve();
				});
			})
			.catch(e => {
				Log.e(e);
				reject(e);
			});
		});
	}

	/**
	 * Get the list of registered users
	 * @ignore If your users base is pretty large, this method is impossible.
	 * @param {boolean} [include=false] Include current user in user's list
	 * @return {Promise<User[], Error>}
	 */
	/* users(include) {
		if(!this.current) {
			return Promise.reject(new Error('Only an authenticated user can list Users.'));
		}
		return DataSync.list('users', User)
			.then(users => {
				return !include && users && this.current ? users.filter(user => user.uid !== this.current.uid) : users;
			})
			.catch(Log.r('Reach~users'));
	}*/

	/**
	 * Get the list of rooms
	 * @ignore If your users base is pretty large, this method is impossible.
	 * @return {Promise<Room[], Error>}
	 */
	/* rooms() {
		if(!this.current) {
			return Promise.reject(new Error('Only an authenticated user can list Rooms.'));
		}
		return DataSync.list('rooms', Room)
			.catch(Log.r('Reach~rooms'));
	}*/

	/**
	 * Get the list of invites
	 * @return {Promise<Invite[], Error>}
	 */
	invites() {
		if(!this.current) {
			return Promise.reject(new Error('Cannot list invites without a User being logged in.'));
		}
		return DataSync.list(`_/invites/${this.current.uid}`, Invite)
			.catch(Log.r('Reach~invites'));
	}

	/**
	 * Register a callback for a specific event
	 * @param {string} event The event name ({@link Events/Reach}). Can be:
	 * - USER_ADDED
	 * - USER_CHANGED
	 * - USER_REMOVED
	 * - ROOM_ADDED
	 * - ROOM_CHANGED
	 * - ROOM_REMOVED
	 * - INVITE_ADDED
	 * - INVITE_CHANGED
	 * @param {function} callback The callback for the event, the arguments depends on the type of event:
	 * - USER_*: callback({@link User} u)
	 * - ROOM_*: callback({@link Room} r)
	 * - INVITE_*: callback({@link Invite} i)
	 * @param {Webcom/api.Query~cancelCallback} [cancelCallback] The error callback for the event, takes an Error as only argument
	 */
	on(event, callback, cancelCallback) {
		const path = Events.reach.toPath(event)(cache.user);
		if(path) {
			const cls = Events.reach.toClass(event);
			const cb = snapData => {
				const d = cls ? new cls(snapData) : null;
				Log.i(`Reach~on(${event})`, d);
				callback(d);
			};
			DataSync.on(path, event, cb, cancelCallback);
			if(!this._callbacks[event]) {
				this._callbacks[event] = [];
			}
			this._callbacks[event].push(cb);
		}
	}

	/**
	 * Create a new room
	 * @param {string} [name] The room name
	 * @param {object} [extra] Extra informations
	 * @param {boolean} [publicRoom=false] Indicates public room
	 * @returns {Promise<Room>}
	 */
	createRoom(name, extra, publicRoom = false) {
		if(!this.current) {
			return Promise.reject(new Error('Cannot create a Room without a User being logged in.'));
		}
		return Room.create(name, extra, publicRoom);
	}

	/**
	 * Get a list of all opened {@link PeerConnection}s
	 * @return {*}
	 */
	get peerConnections () {
		return cache.peerConnections.stacks;
	}

	/**
	 * Get a {@link Room} from its `uid`
	 * @param {string} uid The room's UID
	 * @returns {Promise.<Room>}
	 */
	getRoom (uid) {
		return Room.get(uid);
	}

	/**
	 * Get a {@link User} from its `uid`
	 * @param {string} uid The user's UID
	 * @returns {Promise.<User>}
	 */
	getUser (uid) {
		return User.get(uid);
	}
}

module.exports = Reach;

import media from './util/Media';
import * as DataSync from './util/DataSync';
import * as Log from './util/Log';
import cache from './util/cache';

const _flattenServers = servers => {
	const _expand = servers.map(server => {
		const {username, credential, urls, url} = server;
		const uris = urls || url;
		if(typeof uris !== 'string' ) {
			return uris.map(uri => ({username, credential, urls: uri}));
		}
		return [server];
	});
	return [].concat(..._expand);
};

/**
 * The Reach configuration object
 * @class Config
 */
export default class Config {
	/**
	 * Create configuration
	 * @access protected
	 * @param obj
	 */
	constructor(obj) {
		/**
		 * The default media constraints. These can be overridden when subscribing to a stream.
		 * @type {MediaStreamConstraints}
		 */
		this.constraints = null;

		/**
		 * The id/element dom element that will hold the local video/audio element
		 * @type {string|Element}
		 */
		this.localStreamContainer = null;
		/**
		 * The id/element dom element that will hold the remote video/audio element
		 * @type {string|Element}
		 */
		this.remoteStreamContainer = null;

		/**
		 * The preferred video Codec. Takes a RegExp matching the codec name and sample rate.
		 * Predefined values can be found in {@link Codec/video}
		 * @type {RegExp}
		 * @example <caption>Prefer VP9</caption>
		 * var myReach = new Reach('https://io.datasync.orange.com/base/<my_namespace>', {
		 *  preferredVideoCodec: Reach.codecs.video.VP9
		 * });
		 */
		this.preferredVideoCodec = null;

		/**
		 * The preferred audio Codec. Takes a RegExp matching the codec name and sample rate.
		 * Predefined values can be found in {@link Codec/audio}
		 * @type {RegExp}
		 * @example <caption>Prefer opus</caption>
		 * var myReach = new Reach('https://io.datasync.orange.com/base/<my_namespace>', {
		 *  preferredAudioCodec: Reach.codecs.audio.OPUS
		 * });
		 */
		this.preferredAudioCodec = null;

		// Populate with default values
		this.reset();

		// Populate with data
		this.assign(obj);

		// Read ICE servers from server
		DataSync.get('_/ice').then(snapData => {
			if(snapData) {
				this.iceServers = snapData.val();
				Log.i('ICEServers', this.iceServers.length > 0 ? this.iceServers : 'None');
			}
		}, e => Log.d('ICEServers', e));

		// TODO #Feat: Add boolean prop to request permission on start, sdpEditor (for user defined SDP modifications)
	}

	/**
	 * Assign new conf values
	 * @access protected
	 * @param {object} obj the new conf values
	 */
	assign(obj) {
		Object.keys(obj || {}).forEach(key => {this[key] = obj[key];});
	}

	/**
	 * The log level (DEBUG, INFO, WARN, ERROR)
	 * @type {string}
	 */
	set logLevel(level) {
		cache.logLevel = level;
	}

	/**
	 * The log level (DEBUG, INFO, WARN, ERROR)
	 * @returns {string}
	 */
	get logLevel() {
		return cache.logLevel;
	}

	/**
	 * List of TURN/STUN servers to use for ICE. This list will be merged with the ICE servers declared in the namespace (**_/ice**).
	 * @type {ICEServer[]}
	 */
	set iceServers(servers) {
		Log.d('Config~set~iceServers', servers);
		if(servers) {
			if (!this._iceServers) {
				/**
				 * @ignore
				 */
				this._iceServers = [].concat(servers || []);
			} else {
				// flatten existing
				const _currentServers = _flattenServers(this._iceServers);
				// flatten new
				const _newServers = _flattenServers(servers);
				// Add only the missing servers
				_newServers.forEach(newServer => {
					if(!_currentServers.some(server =>
							server.urls === newServer.urls &&
							server.username === newServer.username &&
							server.credential === newServer.credential)) {
						_currentServers.push(newServer);
					}
				});
				// Re-group by username/credential
				this._iceServers = _currentServers.reduce((previous, current) => {
					const {username, credential, urls} = current;
					const idx = previous.findIndex(s => s.username === username && s.credential === credential);
					if(idx >= 0) {
						previous[idx].urls.push(urls);
					} else {
						previous.push({username, credential, urls: [urls]});
					}
					return previous;
				}, []);
			}
		}
	}

	/**
	 * List of TURN/STUN servers to use for ICE. This list will be merged with the ICE servers declared in the namespace (**_/ice**).
	 * @type {ICEServer[]}
	 */
	get iceServers() {
		return this._iceServers || [
			{
				username: 'admin',
				credential: 'webcom1234',
				urls: [
					'turns:turn1.webcom.orange.com:443',
					'turn:turn1.webcom.orange.com:443?transport=tcp',
					'turn:turn1.webcom.orange.com:3478?transport=tcp'
				]
			}
		];
	}

	/**
	 * Resets configuration to default values
	 * @protected
	 */
	reset () {
		this.assign({
			constraints: media.constraints(),
			logLevel: 'ERROR'
		});
	}
}

import * as DataSync from './util/DataSync';
import * as Log from './util/Log';

/**
 * User device
 * @access protected
 */
export default class Device {
	/**
	 * Create a device
	 * @param {Webcom/api.DataSnapshot|object} snapData The data snapshot
	 * @access protected
	 */
	constructor(snapData) {
		const values = Object.assign({}, snapData.val());
		/**
		 * Device's unique id
		 * @type string
		 */
		this.uid = snapData.name();
		/**
		 * Device's OS
		 * @type {string}
		 */
		this.os = values.os;
		/**
		 * Device's userAgent
		 * @type {string}
		 */
		this.userAgent = values.userAgent;
		/**
		 * Device's last know status
		 * - CONNECTED
		 * - NOT_CONNECTED
		 * - SLEEPING
		 * @type {string}
		 */
		this.status = values.status;
		/**
		 * Device's sdk
		 * @type {{reach: string, webcom: string}}
		 */
		this.sdk = values.sdk;
		// TODO #Feat: Add 'extra' property for unrestricted additional information ?
	}


	/**
	 * Get a device by its user uid and its uid
	 * @access private
	 * @param {string} userUid The user's uid
	 * @param {string} deviceUid The device's uid
	 * @returns {Promise<Device, Error>}
	 */
	static get(userUid, deviceUid) {
		// due to the problem of long list, some uid (uid of participant)
		// can have a : instead of /
		const newUid = userUid.replace(/:/g,'/');
		return DataSync.get(`_/devices/${newUid}/${deviceUid}`)
		.then(snapData => snapData ? new Device(snapData) : null)
		.catch(Log.r('Device#get'));
	}

}

import {ONGOING, ACCEPTED, REJECTED, CANCELED} from './util/constants';
import * as DataSync from './util/DataSync';
import * as Log from './util/Log';
import cache from './util/cache';
import Room from './Room';
import * as Events from '../definitions/Events';

/**
 * Update
 * @param {Invite} invite The invite
 * @param {string} status The new status
 * @param {string} [reason=null] The reason (a message)
 * @param {object} [_ended=null]
 * @access private
 * @returns {Promise<Invite, Error>}
 */
const update = (invite, status, reason = null, _ended = null) => {
	const values = {
		status,
		reason,
		_ended
	};
	if(invite.status !== ONGOING) {
		return Promise.reject(new Error('This invitation has already been answered'));
	}
	return DataSync.update(`_/invites/${invite.to}/${invite.uid}`, values)
		.then(() => {
			Object.keys(values).forEach(prop => {
				invite[prop] = values[prop];
			});
			return Room.get(invite.room);
		})
		.then(() => ({invite}))
		.catch(Log.r('Invite_update'));
};

/**
 * Invitation
 * @public
 */
export default class Invite {

	/**
	 * Create an invite
	 * @param {Webcom/api.DataSnapshot|object} snapData The data snapshot
	 * @access protected
	 */
	constructor(snapData) {
		let values = snapData;
		if(snapData && snapData.val && typeof snapData.val === 'function'){
			values = Object.assign({}, snapData.val(), {uid: snapData.name(), to: snapData.ref().parent().name()});
		}
		/**
		 * Invite's unique id
		 * @type string
		 */
		this.uid = values.uid;
		/**
		 * Invite's sender uid
		 * @type {string}
		 */
		this.from = values.from;
		/**
		 * Invitee's uid
		 * @type {string}
		 */
		this.to = values.to;
		/**
		 * The id of the room associated to the invite
		 * @type {string}
		 */
		this.room = values.room;
		/**
		 * The invitation status :
		 * - ONGOING - The receiver has not yet responded to the invitation
		 * - ACCEPTED - The receiver has accepted the invitation
		 * - REJECTED - The receiver has rejected the invitation
		 * - CANCELED - The sender canceled the invitation
		 * @type {string}
		 */
		this.status = values.status;
		/**
		 * Invite message. This will be either a custom message if the status is ONGOING or a reason when status is CANCELED|REJECTED.
		 * @type {string}
		 */
		this.topic = values.topic;
		/**
		 * Invite creation timestamp
		 * @type {number}
		 */
		this._created = values._created;
		/**
		 * Invite expiration timestamp
		 * @type {number}
		 */
		this._ended = values._ended;
		/**
		 * Invite events callbacks
		 * @type {{}}
		 * @private
		 */
		this._callbacks = {};
	}

	/**
	 * Is this invitation waiting for a reply ?
	 * @type {boolean}
	 */
	get isOnGoing() {
		return this.status === ONGOING;
	}

	/**
	 * Was this invitation rejected ?
	 * @type {boolean}
	 */
	get isRejected() {
		return this.status === REJECTED;
	}

	/**
	 * Was this invitation accepted ?
	 * @type {boolean}
	 */
	get isAccepted() {
		return this.status === ACCEPTED;
	}

	/**
	 * Was this invitation canceled ?
	 * @type {boolean}
	 */
	get isCanceled() {
		return this.status === CANCELED;
	}

	/**
	 * Cancels the invitation. Only the sender can cancel the invitation.
	 * @param {string} [reason] The reason the sender is canceling the invite
	 * @return {Promise<Invite>}
	 */
	cancel(reason) {
		return update(this, CANCELED, reason, DataSync.ts());
	}

	/**
	 * Rejects the invitation. Only the receiver can reject the invitation.
	 * @param {string} [reason] The reason the receiver is rejecting the invite
	 * @return {Promise<Invite>}
	 */
	reject(reason) {
		return update(this, REJECTED, reason, DataSync.ts());
	}

	/**
	 * Accept the invitation. Only the receiver can accept the invitation.
	 * @return {Promise<Invite>}
	 */
	accept() {
		return update(this, ACCEPTED);
	}

	/**
	 * Register a callback for a status update
	 * @param {string} status Can be:
	 * - ACCEPTED
	 * - REJECTED
	 * - CANCELED
	 * @param {function} callback
	 */
	on(status, callback) {
		if(Events.invite.supports(status)) {
			// Register callback
			if (!this._callbacks[status]) {
				this._callbacks[status] = [];
			}
			this._callbacks[status].push(callback);
			// Defined listener & subscribe if needed
			if (!this._listener) {
				/**
				 * Invite status update callback
				 * @type {function}
				 * @private
				 */
				this._listener = snapData => {
					const updated = snapData.val();
					if (updated !== null && updated !== this.status) {
						this.status = updated;
						(this._callbacks[updated] || []).forEach(cb => {
							cb(this);
						});
					}
				};
				DataSync.on(`_/invites/${this.to}/${this.uid}/status`, 'value', this._listener.bind(this));
			}
		}
	}

	/**
	 * Register a callback for all status change events
	 * @param {function} callback
	 */
	onStatusChange(callback) {
		[ACCEPTED, REJECTED, CANCELED].forEach(event => {
			this.on(event, callback);
		});
	}

	/**
	 * Un-register a callback for a status update
	 * @param {string} [status] Can be:
	 * - ACCEPTED
	 * - REJECTED
	 * - CANCELED
	 * - null This will un-register all callbacks
	 * @param {function} [callback]
	 */
	off(status, callback) {
		if(!status) {
			this._callbacks = {};
		} else if(this._callbacks[status]) {
			if(callback) {
				const idx = this._callbacks[status].findIndex(cb => cb === callback);
				if (idx >= 0) {
					this._callbacks.splice(idx, 1);
				}
			} else {
				this._callbacks[status] = [];
			}
		}
		if(![CANCELED, ACCEPTED, REJECTED].some(event => this._callbacks[event] && this._callbacks[event].length > 0)){
			DataSync.off(`_/invites/${this.to}/${this.uid}/status`, 'value');
		}
	}

	/**
	 * Un-register a callback for all status change events
	 * @param {function} [callback]
	 */
	offStatusChange(callback) {
		if(!callback) {
			this.off();
		} else {
			[ACCEPTED, REJECTED, CANCELED].forEach(event => {
				this.off(event, callback);
			});
		}
	}

	/**
	 * Create the invitation & add the user to the participants list
	 * @access protected
	 * @param {User} invitee The user to invite
	 * @param {Room} room The room to invite the user to
	 * @param {string} [message] A message for the invitee
	 */
	static send(invitee, room, message = null) {
		if(!cache.user) {
			return Promise.reject(new Error('Only an authenticated user can send an invite.'));
		}

		const inviteMetaData = {
			from: cache.user.uid,
			room: room.uid,
			status: ONGOING,
			_created: DataSync.ts(),
			topic: message
		};

		return DataSync.push(`_/invites/${invitee.uid}`, inviteMetaData)
			.then(inviteRef => {
				const inviteId = inviteRef.name();
				return new Invite(Object.assign({uid: inviteId, to: invitee.uid}, inviteMetaData));
			})
			.catch(Log.r('Invite#send'));
	}
}

import * as DataSync from './util/DataSync';
import * as Log from './util/Log';

import cache from './util/cache';
/**
 * Instant Message
 * @public
 */
export default class Message {
	/**
	 * Create a message
	 * @param {Webcom/api.DataSnapshot|Object} snapData The data snapshot
	 * @param {string} roomId The message's room id
	 * @access protected
	 */
	constructor(snapData, roomId) {
		const values = Object.assign({}, snapData.val());
		/**
		 * The message unique id
		 * @type {string}
		 */
		this.uid = snapData.name();
		/**
		 * The room uid
		 * @type {string}
		 */
		this.roomId = roomId;
		/**
		 * The message
		 * @type {string}
		 */
		this.text = values.text;
		/**
		 * The message sender
		 * @type {string}
		 */
		this.from = values.from;
		/**
		 * Joined date
		 * @type {number}
		 */
		this._created = values._created;
	}

	/**
	 * Edit the text message. Only the sender or moderator/owner of the room can edit a message.
	 * @param {string} newText The new message
	 * @returns {Promise<Message>}
	 */
	edit(newText) {
		return DataSync.update(`/rooms/${this.roomId}/messages/${this.uid}`, {text: newText})
			.then(() => {
				this.text = newText;
				return this;
			})
			.catch(Log.r('Message~edit'));
	}

	/**
	 * Remove the text message. Only the sender or moderator/owner of the room can remove a message.
	 * @returns {Promise}
	 */
	remove() {
		return DataSync.remove(`/rooms/${this.roomId}/messages/${this.uid}`)
			.catch(Log.r('Message~remove'));
	}

	/**
	 *
	 * @param {Room} room The room to send the message to
	 * @param {string} text The message
	 * @return {Promise<Message>}
	 */
	static send(room, text) {
		if(!cache.user) {
			return Promise.reject(new Error('Cannot send a message to the Room without a User being logged in.'));
		}
		const data = {
			from: cache.user.uid,
			_created: DataSync.ts(),
			text
		};
		return DataSync.push(`_/rooms/${room.uid}/messages`, data)
			.then(pushRef => DataSync.get(`_/rooms/${room.uid}/messages/${pushRef.name()}`))
			.then(snapData => new Message(snapData, room.uid))
			.catch(Log.r('Message#send'));
	}
}

/**
 * {@link Room} participant
 * @public
 */
export default class Participant {
	/**
	 * Create a participant
	 * @param {Webcom/api.DataSnapshot|Object} snapData The data snapshot
	 * @access protected
	 */
	constructor(snapData) {
		const values = Object.assign({}, snapData.val());
		/**
		 * The participant unique id
		 * @type {string}
		 */
		this.uid = snapData.name();
		/**
		 * The Participant's role. Can be:
		 * - OWNER: the owner/creator of the {@link Room}
		 * - MODERATOR: a participant with {@link Room} management Rights
		 * - NONE: basic participant
		 * @type {string}
		 */
		this.role = values.role;
		/**
		 * The participant status Can be:
		 * - NOT_CONNECTED: the participant did not accept the invitation yet and/or did not join the {@link Room} yet
		 * - CONNECTED: the participant is in the {@link Room}
		 * - WAS_CONNECTED: the participant left the {@link Room}
		 * @type {string}
		 */
		this.status = values.status;
		/**
		 * Joined date
		 * @type {number}
		 */
		this._joined = values._joined;
		/**
		 * Participant's userAgent
		 * @type {string}
		 */
		this.userAgent = values.userAgent;
	}
}

