import {NONE, OPENED, CLOSED, CONNECTED, NOT_CONNECTED, WAS_CONNECTED, OWNER} from './util/constants';
import * as DataSync from './util/DataSync';
import cache from './util/cache';
import Participant from './Participant';
import Message from './Message';
import Local from './stream/Local';
import Invite from './Invite';
import * as Events from '../definitions/Events';
import * as Log from './util/Log';
import {REJECTED, CANCELED} from './util/constants';

const _joinRoom = (room, role) => {
	const uid = cache.user.uid.split('/').join(':');

	if (room.status !== CLOSED) {
		const participant = {
			status: CONNECTED,
			userAgent: cache.userAgent,
			_joined: DataSync.ts()
		};
		if(role) {
			participant.role = role;
		}
		Log.w('Room#join', [participant, `_/rooms/${room.uid}/participants/${uid}`]);
		return DataSync
		// .update(`_/rooms/${room.uid}/participants/${cache.user.uid}`, participant)
		.update(`_/rooms/${room.uid}/participants/${uid}`, participant)
			.then(() => {
				/* DataSync
                                    .onDisconnect(`_/rooms/${room.uid}/participants/${shortUserId}/status`)
                                    .set(WAS_CONNECTED);*/
				return room;
			});
	}
	return Promise.reject(new Error('can\'t join a close room'));
};

/**
 * Room information
 * @access public
 */
export default class Room {
	/**
	 * Create a room
	 * @param {Webcom/api.DataSnapshot|Object} snapData The data snapshot
	 * @access protected
	 */
	constructor(snapData, roomUid) {
		let values = snapData;
		if(snapData && snapData.val && typeof snapData.val === 'function'){
			// values = Object.assign({}, snapData.val(), {uid: snapData.name()});
			values = Object.assign({}, snapData.val(), {uid: roomUid});
		}
		/**
		 * The room unique id
		 * @type {string}
		 */
		this.uid = values.uid;
		/**
		 * The room name
		 * @type {string}
		 */
		this.name = values.name;
		/**
		 * The local stream of the room
		 * @type {Local}
		 */
		this.localStream = {};
		/**
		 * The room owner uid
		 * @type {string}
		 */
		this.owner = values.owner;
		/**
		 * The room status:
		 * - OPENED
		 * - CLOSED
		 * @type {string}
		 */
		this.status = values.status;

		/**
		 * Indicates that the room is public so all users can join
		 * @type {boolean}
		 */
		this._public = !!values._public;

		/**
		 * Additional room informations
		 * @type {Object}
		 */
		this.extra = values.extra;

		/**
		 * List of declared callbacks
		 * @type {Object}
		 */
		this._callbacks = {};
	}

	/**
	 * Get the list of participants.
	 * This will only work if the user is either a participant or the owner of the room.
	 * @returns {Promise<Participant[], Error>}
	 */
	participants() {
		return DataSync.list(`_/rooms/${this.uid}/participants`, Participant, this.uid);
	}

	/**
	 * Get the list of messages.
	 * This will only work if the user is either a participant or the owner of the room.
	 * @return {Promise<Message[], Error>}
	 */
	messages() {
		return DataSync.list(`_/rooms/${this.uid}/messages`, Message, this.uid);
	}

	/**
	 * Get the list of streams
	 * @returns {Promise}
	 * @access private
	 */
	_streams(localStreams) {
		if(!cache.user) {
			return Promise.reject(new Error('Only an authenticated user can list a Room\'s streams.'));
		}
		return DataSync.get(`_/rooms/${this.uid}/streams`)
			.then(snapData => {
				const values = snapData.val();
				Log.d('Rooms~_streams', values);
				if(values) {
					return Object.keys(values).map(key => Object.assign({uid: key, roomId: this.uid}, values[key]));
				}
				return [];
			})
			.then(streams => streams.filter(stream => {
				return localStreams === (stream.device === cache.device && stream.from === cache.user.uid);
			}))
			.then(streams => streams.map(cache.streams[`get${localStreams ? 'Shared' : 'Remote'}`].bind(cache.streams)))
			.then(streams => streams.filter(stream => stream !== null));
	}

	/**
	 * Get the list of locally published streams. The streams published with another device won't be visible here
	 * This will only work if the user is either a participant or the owner of the room.
	 * @return {Promise<Local[], Error>}
	 */
	localStreams() {
		return this._streams(true)
			.catch(Log.r('Room~localStreams'));
	}

	/**
	 * Get the list of remotely published streams.
	 * This will only work if the user is either a participant or the owner of the room.
	 * @return {Promise<Remote[], Error>}
	 */
	remoteStreams() {
		return this._streams(false)
			.catch(Log.r('Room~remoteStreams'));
	}

	/**
	 * Invite users to the room. this will only work if the current User is the owner or a moderator of this Room.
	 * This will create the invitation and add the user to the participants list.
	 * @param {User[]} users the users to invite
	 * @param {string} [role='NONE'] the role of the invitee
	 * @param {string} [message] a message to add to the invite
	 * @return {Promise<{room: Room, invites: Invite[]}, Error>}
	 */
	invite(users, role = NONE, message) {
		const
			_path = user => `_/rooms/${this.uid}/participants/${user.uid}`,
			_data = {
				status: NOT_CONNECTED,
				role: role || NONE
			};
		// Add users as participant so they can join the room
		return Promise.all(users.map(user => DataSync.set(_path(user), _data)))
			// Send invites
			.then(() => Promise.all(users.map(user => Invite.send(user, this, message))))
			.then(invites => {
				const removeParticipant = invite => DataSync.remove(`_/rooms/${invite.room}/participants/${invite.to}`);
				invites.forEach(invite => {
					invite.on(REJECTED, removeParticipant);
					invite.on(CANCELED, removeParticipant);
				});
				return {room: this, invites};
			})
			.catch(e => {
				Log.e('Room~invite', e);
				users.forEach(user => DataSync.remove(`_/rooms/${this.uid}/participants/${user.uid}`));
				return Promise.reject(e);
			});
	}

	/**
	 * Register a callback for a specific event
	 * @param {string} event The event name ({@link Events/Room}):
	 * - PARTICIPANT_ADDED: a participant is added to the room
	 * - PARTICIPANT_CHANGED: a participant changes his status (join)
	 * - PARTICIPANT_REMOVED: a participant leave the room
	 * - MESSAGE_ADDED: new instant message
	 * - MESSAGE_CHANGED: an existing message has been modified (moderation)
	 * - MESSAGE_REMOVED: a message has been removed (moderation)
	 * - STREAM_PUBLISHED: a participant published a new Stream
	 * - STREAM_CHANGED: a participant changes his published Stream (moderation, type, mute...)
	 * - STREAM_UNPUBLISHED: a participant stops the publication of his Stream
	 * @param {function} callback The callback for the event, the arguments depends on the type of event:
	 * - PARTICIPANT_* : callback({@link Participant} p [, Error e])
	 * - MESSAGE_* : callback({@link Message} m [, Error e])
	 * - STREAM_* : callback({@link Remote} s [, Error e])
	 * @param {Webcom/api.Query~cancelCallback} cancelCallback The error callback for the event, takes an Error as only argument
	 */
	on(event, callback, cancelCallback) {
		const
			path = Events.room.toPath(event)(this),
			obj = Events.room.toClass(event);
		if(path && obj) {
			const typedCallback = snapData => {
				if(!/^STREAM_/i.test(event) || !snapData) {
				// if(/^MESSAGE_/i.test(event) || !snapData) {
					Log.i(`Room~on(${event})`, snapData ? new obj(snapData) : null);
					callback(snapData ? new obj(snapData) : null);
				} else if(cache.user) {
					const streamData = Object.assign({uid: snapData.name(), roomId: this.uid}, snapData.val());
					if(streamData.from !== cache.user.uid || streamData.device !== cache.device) {
						const remoteStream = cache.streams.getRemote(streamData);
						Log.i(`Room~on(${event})`, remoteStream);
						callback(remoteStream);
					}
				}
			};
			DataSync.on(path, event, typedCallback, cancelCallback);
			if(!this._callbacks[event]) {
				this._callbacks[event] = [];
			}
			this._callbacks[event].push(typedCallback);
		}
	}

	/**
	 * Send an instant message
	 * @param {string} message The message to send
	 * @return {Promise<Message>}
	 */
	sendMessage(message) {
		return Message.send(this, message);
	}

	/**
	 * Publish a local stream
	 * @param {string} type The stream type, see {@link StreamTypes} for possible values
	 * @param {Element} [localStreamContainer] The element the stream is attached to. Can be null if already specified in {@link Config}.
	 * @param {MediaStreamConstraints} [constraints] The stream constraints. If not defined, the constraints defined in {@link Config} will be used.
	 * @returns {Promise<Local, Error>}
	 */
	share(type, localStreamContainer, constraints) {
		Log.i('Room~share', {type, localStreamContainer, constraints});
		return Local.share(this.uid, type, localStreamContainer, constraints);
	}

	/**
	 * get a local stream in video tag
	 * @param {string} type The stream type, see {@link StreamTypes} for possible values
	 * @param {Element} [localStreamContainer] The element the stream is attached to. Can be null if already specified in {@link Config}.
	 * @param {MediaStreamConstraints} [constraints] The stream constraints. If not defined, the constraints defined in {@link Config} will be used.
	 * @returns {Promise<Local, Error>}
	 */
	getLocalVideo(type, localStreamContainer, constraints) {
		Log.i('Room~getLocalVideo', {type, localStreamContainer, constraints});
		return Local.getLocalVideo(this.uid, type, localStreamContainer, constraints)
		.then( localStream => {
			this.localStream = localStream;
			return localStream;
		});
	}

	/**
	 * publish a local stream
	 * @returns {Local}
	 */
	publish() {
		Log.i('Room~publish Local');
		return Local.publish(this.localStream);
	}

	/**
	 * Join the room. Sets the connected status of the current participant to CONNECTED.
	 * @return {Promise}
	 */
	join() {
		Log.i('Room~join', this);
		if(!cache.user) {
			return Promise.reject(new Error('Only an authenticated user can join a Room.'));
		}
		return _joinRoom(this).catch(Log.r('Room~join'));
	}

	/**
	 * Leave the room. Sets the connected status of the current participant to WAS_CONNECTED, deletes medias and callbacks, closes WebRTC stacks in use.
	 * @return {Promise}
	 */
	leave() {
		if(!cache.user) {
			return Promise.reject(new Error('Only an authenticated user can leave a Room.'));
		}
		Log.i('Room~leave', this);
		// Cancel onDisconnect
		const uid = cache.user.uid.split('/').join(':');
		DataSync.onDisconnect(`_/rooms/${this.uid}/participants/${uid}/status`).cancel();

		// Disconnect user's callbacks
		Object.keys(this._callbacks).forEach(event => {
			DataSync.off(Events.room.toPath(event)(this), event);
		});
		// Unpublish all published local streams
		this.localStreams().then(localStreams => localStreams.forEach(localStream => localStream.close()));
		// Unsubscribe all remote streams
		this.remoteStreams().then(remoteStreams => remoteStreams.forEach(remoteStream => remoteStream.unSubscribe()));
		// Update status
		return DataSync.set(`_/rooms/${this.uid}/participants/${uid}/status`, WAS_CONNECTED)
		// return DataSync.set(`_/rooms/${this.uid}/participants/${shortUserId}/status`, WAS_CONNECTED)
			.catch(Log.r('Room~leave'));
	}

	/**
	 * Leaves & close the Room. Only the owner/moderator can close a room.
	 * @return {Promise}
	 */
	close() {
		Log.i('Room~close', this);
		this.status = CLOSED;
		return this.leave()
			.then(() => {
				return DataSync.update(`rooms/${this.uid}`, {
					status: CLOSED,
					_closed: DataSync.ts()
				});
			})
			.then(() => {
				return DataSync.remove(`_/rooms/${this.uid}`);
			})
			.catch(Log.r('Room~close'));
	}

	/**
	 * Create a room
	 * @access protected
	 * @param {String} [name] The room name
	 * @param {object} [extra=null] Extra informations
	 * @param {boolean} [publicRoom=false] Indicates public room
	 * @returns {Promise<Room, Error>}
	 */
	static create (name, extra = null, publicRoom = false) {
		if(!cache.user) {
			return Promise.reject(new Error('Only an authenticated user can create a Room.'));
		}

		const
			roomMetaData = {
				owner: cache.user.uid,
				_public: publicRoom,
				name: name || `${cache.user.name}-${Date.now()}`
			},
			roomFullMetaData = Object.assign({
				status: OPENED,
				_created: DataSync.ts(),
				extra
			}, roomMetaData);

		let room = null;
		// Create public room infos
		// return DataSync.push('rooms', roomFullMetaData)
		const id1 = Math.floor(Math.random() * 1000);
		const id2 = Math.floor(Math.random() * 1000);
		return DataSync.push(`rooms/${id1}/${id2}`, roomFullMetaData)
			// Create private room infos
			.then(roomRef => {
				room = new Room(Object.assign({uid: `${id1}/${id2}/${roomRef.name()}`}, roomFullMetaData));
				return DataSync.update(`_/rooms/${room.uid}/meta`, roomMetaData);
			})
			// Join the room
			.then(() => _joinRoom(room, OWNER))
			.catch(Log.r('Room#create'));
	}

	/**
	 * Get a {@link Room} from its `uid`
	 * @access protected
	 * @param uid
	 * @returns {Promise.<Room>}
	 */
	static get (uid) {
		return DataSync.get(`rooms/${uid}`)
			.then(snapData => {
				if(snapData.val()) {
					return new Room(snapData, uid);
				}
			});
	}
}

import * as DataSync from './util/DataSync';
import cache from './util/cache';
import * as Log from './util/Log';
import Room from './Room';
import Device from './Device';
import {CONNECTED, NOT_CONNECTED} from './util/constants';
import Webcom from 'webcom/webcom';

let initializing = false;

/**
 * User informations
 * @public
 */
export default class User {
	/**
	 * Create a user
	 * @param {Webcom/api.DataSnapshot|object} snapData The data snapshot
	 * @access protected
	 */
	constructor(snapData, userId) {
		const values = Object.assign({}, snapData.val());
		/**
		 * User's unique id
		 * @type {string}
		*/
		// this.uid = snapData.name();
		this.uid = userId;
		/**
		 * User's display name
		 * @type {string}
		 */
		this.name = values.name;
		/**
		 * User's status
		 * @type {string}
		 */
		this.status = values.status;
		/**
		 * User's last know connection ts
		 * @type {number}
		 */
		this.lastSeen = values.lastSeen;
		/**
		 * Indicates if the user is an anonymous user
		 * @type {boolean}
		 */
		this.anonymous = /^anonymous/.test(values.provider);
		// TODO #Feat: Add 'extra' property for unrestricted additional information ?
	}

	/**
	 * Invite a user directly. This will create a new Room, log you in it & invite the user.
	 * @param {string} [message] a message to add to the invite
	 * @return {Promise<{room: Room, invite: Invite}, Error>}
	 */
	invite(message) {
		if(!cache.user) {
			return Promise.reject(new Error('Only an authenticated user can invite another User.'));
		}
		return Room.create(`${cache.user.uid}-${this.uid}`)
			.then(room => {
				return room.invite([this], null, message);
			})
			.then(data => {
				return {room: data.room, invite: data.invites[0]};
			})
			.catch(Log.r('User~invite'));
	}

	/**
	 * List Users's devices. Only for current user.
	 * @access protected
	 * @return {Promise<Device[], Error>}
	 */
	devices() {
		return DataSync.list(`_/devices/${this.uid}`, Device);
	}

	/**
	 * Init the current user
	 * @access protected
	 * @param {json} auth The user's identity (webcom JSON structure)
	 * @param {string} [name] The user's display name
	 * @returns {Promise<User, Error>}
	 */
	static init (auth, name) {
		const id1 = Math.floor(Math.random() * 1000);
		const id2 = Math.floor(Math.random() * 1000);
		const uid = `${id1}/${id2}/${auth.uid}`;
		const userUid = `${id1}:${id2}:${auth.uid}`;
		// const uid = auth.uid;
		if(!initializing) {
			initializing = true;
			const d = {status: CONNECTED, lastSeen: DataSync.ts(), provider: auth.provider};
			if(name) {
				Object.assign(d, {name});
			}
			let deviceId = Webcom.INTERNAL.PersistentStorage.get(uid);
			return DataSync.update(`users/${uid}`, d)
				// Register current device
				.then(() => {
					const deviceMetadata = {
						status: CONNECTED,
						sdk: {
							reach: SDK_VERSION,
							webcom: Webcom.SDK_VERSION
						},
						userAgent: navigator.userAgent
					};
					cache.userAgent = deviceMetadata.userAgent;
					if(deviceId) {
						return DataSync.update(`_/devices/${uid}/${deviceId}`, deviceMetadata);
					}
					return DataSync.push(`_/devices/${uid}`, deviceMetadata);
				})
				// Save device
				.then(deviceRef => {
					if (!deviceId) {
						deviceId = deviceRef.name();
						Webcom.INTERNAL.PersistentStorage.set(uid, deviceId);
					}
					cache.device = deviceId;
				})
				// Add onDisconnect actions
				.then(() => {
					// Disconnect device
					DataSync.onDisconnect(`_/devices/${uid}/${deviceId}/status`).set(NOT_CONNECTED);
					// Update user status
					DataSync.onDisconnect(`users/${uid}`).update({
						status: NOT_CONNECTED,
						lastSeen: DataSync.ts()
					});
				})
				// Get user
				// .then(() => User.get(uid))
				.then(() => User.get(userUid))
				.then(user => {
					initializing = false;
					return user;
				})
				.catch(e => {
					Log.e(e);
					initializing = false;
					return Promise.reject(e);
				});
		}
		// return User.get(uid);
		return User.get(userUid);
	}

	/**
	 * Disconnect the current user
	 * @access protected
	 * @param {User} user The current user
	 * @returns {Promise}
	 */
	static disconnect(user) {
		// Cancel onDisconnect
		// due to the problem of long list, uids have a : instead of /
		const userUid = user.uid.replace(/:/g,'/');
		// DataSync.onDisconnect(`_/devices/${user.uid}/${cache.device}/status`).cancel();
		// DataSync.onDisconnect(`users/${user.uid}`).cancel();
		DataSync.onDisconnect(`_/devices/${userUid}/${cache.device}/status`).cancel();
		DataSync.onDisconnect(`users/${userUid}`).cancel();
		if(user.anonymous) {
			return DataSync.remove(`_/devices/${userUid}`)
				.then(() => DataSync.get(`_/invites/${userUid}`))
				.then(invites => {
					const inviteIds = [];
					invites.forEach(invite => {
						inviteIds.push(invite.name());
					});
					return Promise.all(inviteIds.map(inviteId => DataSync.remove(`_/invites/${userUid}/${inviteId}`)));
				})
				// TODO refactor data model for invites so we can delete _/invites/${user.uid}
				// .then(() => DataSync.remove(`_/invites/${user.uid}`))
				.then(() => DataSync.remove(`users/${userUid}`))
				.then(() => {
					Webcom.INTERNAL.PersistentStorage.remove(userUid);
				})
				.catch(Log.r('User#anonymous_disconnect'));
		}
		return DataSync.set(`_/devices/${userUid}/${cache.device}/status`, NOT_CONNECTED)
			.then(() => DataSync.get(`_/devices/${userUid}`))
			.then(devices => {
				// Only change user's status if no other device connected
				const hasConnectedDevices = devices.forEach(device => {
					return (new RegExp(`^${CONNECTED}$`)).test(device.val().status);
				});
				if(!hasConnectedDevices) {
					return DataSync.update(`users/${userUid}`, {status: NOT_CONNECTED});
				}
				return true;
			})
			.catch(Log.r('User#disconnect'));
	}

	/**
	 * Get a user by its uid
	 * @access private
	 * @param {string} uid The user's uid
	 * @returns {Promise<User, Error>}
	 */
	static get(uid) {
		// due to the problem of long list, uids have a : instead of /
		const userUid = uid.replace(/:/g,'/');
		return DataSync.get(`users/${userUid}`)
		// .then(snapData => snapData ? new User(snapData, newUid) : null)
		.then(snapData => snapData ? new User(snapData, uid) : null)
		.catch(Log.r('User#get'));
	}
}

import StreamTypes from '../../definitions/StreamTypes';
import * as Log from '../util/Log';
import cache from '../util/cache';
import * as DataSync from '../util/DataSync';
import Media from '../util/Media';
import {NONE, CLOSED, CLOSING, CONNECTED} from '../util/constants';

const _facingModes = [Media.facingMode.USER, Media.facingMode.ENVIRONMENT];

const _getConstraintValue = (constraints, prop) => {
	return constraints[prop].exact || constraints[prop].ideal || constraints[prop];
};

const _setConstrainValue = (constraints, prop, other, value) => {
	constraints[prop] = {exact: value};
	delete constraints[other];
};

/**
 * The local stream
 */
export default class Local {
	/**
	 * @access protected
	 * @param {object} values
	 */
	constructor(values) {
		/**
		 * The uid of the room the stream is published in
		 * @type {string}
		 */
		this.roomId = values.roomId;
		/**
		 * The uid of this stream
		 * @type {string}
		 */
		this.uid = values.uid;
		/**
		 * The type of the stream
		 * @type {string}
		 */
		this.type = values.type;
		/**
		 * Indicates if a track is muted
		 * @type {{audio: boolean, video: boolean}}
		 */
		this.muted = Object.assign({audio: false, video: false}, values.muted);
		/**
		 * The local DOM container element where the {@link Local~media} is displayed
		 * @type {Element}
	  */
		this.container = values.container || cache.config.localStreamContainer;
		/**
	  * The local DOM media element where the {@link Local~media} is displayed
	  * @type {Element}
	  */
		this.node = null;
		/**
		 * List of the PeerConnections associated to this local stream
		 * @type {PeerConnection[]}
		 */
		this.peerConnections = [];
		/**
		 * Local stream status
		 * @type {string}
		 */
		this.status = NONE;
		/**
		 * is the video is loaded int the local DOM media element
		 * @type {boolean}
		 */
		this.isVideoLoaded = false;
		/**
		 * @access private
		 * @type {{audio: string, video: string}}
		 */
		this._inputs = {};

		// Set constraints
		this.constraints = values.constraints;
	}

	/**
	 * The Media Constraints. (defaults to global config)
	 * @param {MediaConstraints} constraints
	 */
	set constraints(constraints) {
		const
			values = constraints || cache.config.constraints,
			defaultConstraints = Media.constraints();
		['audio', 'video'].forEach(type => {
			if(!~this.type.indexOf(type)) {
				values[type] = false;
			} else if(!values[type]){
				values[type] = defaultConstraints[type];
			}
			if(values[type].deviceId || values[type].facingMode) {
				this._inputs[type] = _getConstraintValue(
					values[type],
					values[type].facingMode ? 'facingMode' : 'deviceId'
				);
			}
		});
		Log.d('Local~set#contraints', values);
		/**
		 * @ignore
		 */
		this._constraints = values;
	}

	/**
	 * The Media Constraints. (defaults to global config)
	 * @type {MediaConstraints}
	 */
	get constraints() {
		return this._constraints;
	}

	/**
	 * Updates the stream constraints and retrieve the new MediaStream
	 * @param constraints
	 * @returns {*|Promise.<TResult>}
	 */
	updateConstraints(constraints) {
		Log.d('Local~updateConstraints', constraints);
		this.constraints = constraints;
		return navigator.mediaDevices.getUserMedia(this.constraints)
			.then(media => {
				['audio', 'video'].forEach(kind => {
					const constraintsValue = this.constraints[kind];
					if(constraintsValue) {
						if (constraintsValue.deviceId || constraintsValue.facingMode) {
							this._inputs[kind] = _getConstraintValue(
								constraintsValue,
								constraintsValue.facingMode ? 'facingMode' : 'deviceId'
							);
						}
					}
				});
				this.media = media;
			});
	}

	/**
	 * The associated MediaStream
	 * @type {MediaStream}
	 */
	set media (mediaStream) {
		if(mediaStream) {
			if(!(mediaStream instanceof MediaStream)) {
				throw new Error('The media MUST be a MediaStream');
			}

			const checkDevices = {};
			mediaStream.getTracks().forEach(track => {
				// Reset mute
				track.enabled = !this.muted[track.kind];
				// Get device label
				if(!this._inputs[track.kind]) {
					checkDevices[track.kind] = track.label;
				}
			});
			// Try to get deviceId from label
			if(Object.keys(checkDevices).length) {
				Media.devices().then(devices => {
					Object.keys(checkDevices).forEach(kind => {
						if(devices[`${kind}input`]){
							const deviceIds = devices[`${kind}input`]
								.filter(device => device.label.length && device.label === checkDevices[kind]);
							if(deviceIds.length === 1 && !this._inputs[kind]) {
								this._inputs[kind] = deviceIds[0].deviceId;
							}
							if (deviceIds.length === 0
								&& devices[`${kind}input`][0].label === ''
								&& !this._inputs[kind]) {
								// from a webview, the label is not delivered
								this._inputs[kind] = devices[`${kind}input`][0].deviceId;
							}
						}
					});
				});
			}
			// Display
			this.node = Media.attachStream(mediaStream, this.container, this.node, 0);
			this.node.onloadeddata = () => {
				this.isVideoLoaded = true;
			};
			this.status = CONNECTED;
			Log.d('Local~set media', {mediaStream}, this.node);
			// Renegotiate
			this.peerConnections.forEach(peerConnection => peerConnection.renegotiate(this._media, mediaStream));
		} else if(this.media && !mediaStream) {
			// Remove node
			this.node.srcObject = null;
			this.container.removeChild(this.node);
			this.node = null;
			// Stop stream
			this.media.getTracks().forEach(track => track.stop());
			// Close PeerConnections
			this.peerConnections.forEach(peerConnection => peerConnection.close());
		}
		// Save
		/**
		 * @ignore
		 */
		this._media = mediaStream;
	}

	/**
	 * The associated MediaStream
	 * @type {MediaStream}
	 */
	get media () {
		return this._media;
	}

	/**
	 * Mute a track of a Stream
	 * @param {string} [track=AUDIO] The track to mute. (AUDIO, VIDEO, AUDIO_VIDEO)
	 * @param {boolean} [state=true] true for mute & false for un-mute
	 * @example <caption>mute video</caption>
	 * stream.mute(Reach.t.VIDEO)
	 * @example <caption>mute audio</caption>
	 * stream.mute(Reach.t.AUDIO)
	 * // or
	 * stream.mute()
	 */
	mute(track = StreamTypes.AUDIO, state = true) {
		Log.d('mute', track, state);
		let audio = this.muted.audio, video = this.muted.video, tracks;
		switch (track) {
			case StreamTypes.AUDIO:
				audio = state;
				tracks = this.media.getAudioTracks();
				break;
			case StreamTypes.VIDEO:
			case StreamTypes.SCREEN_SHARING:
				video = state;
				tracks = this.media.getVideoTracks();
				break;
			case StreamTypes.AUDIO_VIDEO:
				audio = state;
				video = state;
				tracks = this.media.getTracks();
				break;
		}
		// Mute media tracks
		tracks.forEach(track => {track.enabled = !state;});
		// Signal subscribers
		this.muted = {audio, video};
		DataSync.set(`_/rooms/${this.roomId}/streams/${this.uid}/muted`, this.muted);
	}

	/**
	 * Un-mute a track of a Stream
	 * @param {string} [track=AUDIO] The track to mute. (AUDIO, VIDEO, AUDIO_VIDEO)
	 * @example <caption>Un-mute video</caption>
	 * stream.unMute(Reach.t.VIDEO)
	 * @example <caption>Un-mute audio</caption>
	 * stream.unMute(Reach.t.AUDIO)
	 * // or
	 * stream.unMute()
	 */
	unMute(track) {
		this.mute(track, false);
	}

	/**
	 * Removes stream for published list, closes associated PeerConnections and stops current MediaStream
	 * @returns {Promise}
	 */
	close() {
		if(!~[CLOSED, CLOSING].indexOf(this.status)) {
			this.status = CLOSING;
			// Stop listening to Subscribers
			const path = `_/rooms/${this.roomId}/subscribers/${this.uid}`;
			DataSync.off(path, 'child_added');
			DataSync.off(path, 'child_removed');
			// Cancel onDisconnects
			DataSync.onDisconnect(`_/rooms/${this.roomId}/streams/${this.uid}`).cancel();
			DataSync.onDisconnect(`_/rooms/${this.roomId}/subscribers/${this.uid}`).cancel();
			// Remove subscribers
			DataSync.remove(path);
			// Remove stream
			DataSync.remove(`_/rooms/${this.roomId}/streams/${this.uid}`);
			this.media = null;
			// Close
			this.status = CLOSED;
		}
		return Promise.resolve(this.status);
	}

	/**
	 * Switch video input device
	 * @param {string} [deviceId] A video input device Id or the `facingMode` value
	 * @returns {Promise<Local, Error>}
	 */
	switchCamera(deviceId) {
		return this._switchDevice(StreamTypes.VIDEO, deviceId);
	}

	/**
	 * Switch audio input device
	 * @param {string} [deviceId] A audio input device Id
	 * @returns {Promise<Local, Error>}
	 */
	switchMicrophone(deviceId) {
		return this._switchDevice(StreamTypes.AUDIO, deviceId);
	}

	/**
	 * Switch input device
	 * @access private
	 * @param {string} kind The kind of device to switch
	 * @param {string} [deviceId] An input device id
	 * @returns {Promise<Local, Error>}
     */
	_switchDevice(kind, deviceId) {
		Log.d('Local~_switchDevice', kind, deviceId);
		if(this.media.getTracks().some(track => track.kind === kind)) {
			let next = Promise.resolve(deviceId);
			const currentModeIdx = _facingModes.indexOf(this._inputs[kind]);
			if(!deviceId && !!~currentModeIdx) {
				// Loop facingModes
				next = Promise.resolve(_facingModes[(currentModeIdx + 1) % _facingModes.length]);
			} else if(!~_facingModes.indexOf(deviceId)) {
				// Loop deviceIds
				next = Media.devices()
					.then(d => {
						// devices IDs
						const devices = d[`${kind}input`].map(mediaDevice => mediaDevice.deviceId);
						// Sort to ensure same order
						devices.sort();
						// New device
						let nextDevice = deviceId;
						if(deviceId && !devices.some(device => device === deviceId)) {
							return Promise.reject(new Error(`Unknown ${kind} device`));
						}
						if(!deviceId && devices.length > 1) {
							let idx = this._inputs[kind] ? devices.findIndex(v => v === this._inputs[kind], this) : 0;
							nextDevice = devices[++idx % devices.length];
						}
						return nextDevice;
					});
			} else {
				next = Promise.resolve(deviceId);
			}

			return next
				.then(device => {
					if(this._inputs[kind] !== device) {
						// Update video streams
						this._inputs[kind] = device;
						// Stop tracks
						this.media.getTracks().forEach(track => track.stop());
						// Update constraints
						const constraints = Object.assign({}, this.constraints);
						let props = ['facingMode', 'deviceId'];
						if(!~_facingModes.indexOf(device)) {
							props = props.reverse();
						}
						_setConstrainValue(constraints[kind], props[0], props[1], device);
						Log.d('Local~_switchDevice', kind, constraints);
						return this.updateConstraints(constraints);
					}
				})
				.then(() => this);
		}
		return Promise.reject(new Error(`Current stream does not contain a ${kind} track`));
	}
	/**
	 * Publish a local stream
	 * @access protected
	 * @param {string} roomId The room Id
	 * @param {string} type The stream type, see {@link StreamTypes} for possible values
	 * @param {?Element} container The element the stream is attached to.
	 * @param {?MediaStreamConstraints} [constraints] The stream constraints. If not defined the constraints defined in ReachConfig will be used.
	 * @returns {Promise<Local, Error>}
	 */
	static share(roomId, type, container, constraints) {
		if(!cache.user) {
			return Promise.reject(new Error('Only an authenticated user can share a stream.'));
		}
		const streamMetaData = {
				from: cache.user.uid,
				device: cache.device,
				type
			},
			sharedStream = new Local(Object.assign({roomId, constraints, container}, streamMetaData));
		Log.d('Local~share', {sharedStream});
		return navigator.mediaDevices.getUserMedia(sharedStream.constraints)
			.then(media => {
				sharedStream.media = media;
			})
			// Got MediaStream, publish it
			.then(() => DataSync.push(`_/rooms/${roomId}/streams`, streamMetaData))
			.then(streamRef => {
				sharedStream.uid = streamRef.name();
				if (/video/i.test(sharedStream.type)) {
					if (sharedStream.isVideoLoaded) {
						const streamSize = {
							height: sharedStream.node.videoHeight,
							width: sharedStream.node.videoWidth,
						};
						streamRef.update(streamSize);
					} else {
						sharedStream.node.onloadeddata = function() {
							const streamSize = {
								height: sharedStream.node.videoHeight,
								width: sharedStream.node.videoWidth,
							};
							streamRef.update(streamSize);
						};
					}
				}
				if (/video/i.test(sharedStream.type)) {
					window.addEventListener('resize', (() => {
						if (sharedStream.node != null) {
							const streamSize = {
								height: sharedStream.node.videoHeight,
								width: sharedStream.node.videoWidth,
							};
							streamRef.update(streamSize);
						}
					}));
				}
				// Save sharedStream
				cache.streams.shared[sharedStream.uid] = sharedStream;
				// Remove shared stream on Disconnect
				DataSync.onDisconnect(`_/rooms/${roomId}/streams/${sharedStream.uid}`).remove();
				// Remove shared stream on Disconnect
				DataSync.onDisconnect(`_/rooms/${roomId}/subscribers/${sharedStream.uid}`).remove();
				// Start listening to subscribers
				const
					path = `_/rooms/${sharedStream.roomId}/subscribers/${sharedStream.uid}`,
					value = snapData => Object.assign({device: snapData.name()}, snapData.val() || {});
				DataSync.on(path, 'child_added',
					snapData => {
						const subscriber = value(snapData);
						Log.d('Local~subscribed', subscriber);
						cache.peerConnections.offer(sharedStream, subscriber)
							.then(pc => sharedStream.peerConnections.push(pc));
					},
					Log.e.bind(Log)
				);
				DataSync.on(path, 'child_removed',
					snapData => {
						const subscriber = value(snapData);
						Log.d('Local~un-subscribed', subscriber);
						const closedPC = cache.peerConnections.close(sharedStream.uid, subscriber.device);
						sharedStream.peerConnections = sharedStream.peerConnections.filter(pc => pc !== closedPC);
					},
					Log.e.bind(Log)
				);
				Log.d('Local~shared', {sharedStream});
				return sharedStream;
			});
	}

	/**
	 * Get a local stream
	 * @access protected
	 * @param {string} roomId The room Id
	 * @param {string} type The stream type, see {@link StreamTypes} for possible values
	 * @param {?Element} container The element the stream is attached to.
	 * @param {?MediaStreamConstraints} [constraints] The stream constraints. If not defined the constraints defined in ReachConfig will be used.
	 * @returns {Promise<Local, Error>}
	 */
	static getLocalVideo(roomId, type, container, constraints) {
		if(!cache.user) {
			return Promise.reject(new Error('Only an authenticated user can share a stream.'));
		}
		const streamMetaData = {
				from: cache.user.uid,
				device: cache.device,
				userAgent: cache.userAgent,
				type
			},
			sharedStream = new Local(Object.assign({roomId, constraints, container}, streamMetaData));
		sharedStream.streamMetaData = streamMetaData;
		Log.d('Local~getLocalVideo', {sharedStream});
		return navigator.mediaDevices.getUserMedia(sharedStream.constraints)
			.then(media => {
				sharedStream.media = media;
				return sharedStream;
			});
	}

	/**
	 * Publish a local stream
	 * @access protected
	 * @returns {Local}
	 */
	static publish(sharedStream) {
		Log.d('Local~publish');
		const roomId = sharedStream.roomId;
		return DataSync.push(`_/rooms/${roomId}/streams`, sharedStream.streamMetaData)
			.then(streamRef => {
				sharedStream.uid = streamRef.name();
				if (sharedStream.isVideoLoaded) {
					const streamSize = {
						height: sharedStream.node.videoHeight,
						width: sharedStream.node.videoWidth,
					};
					streamRef.update(streamSize);
				} else {
					sharedStream.node.onloadeddata = function() {
						const streamSize = {
							height: sharedStream.node.videoHeight,
							width: sharedStream.node.videoWidth,
						};
						streamRef.update(streamSize);
					};
				}
				window.addEventListener('resize', (() => {
					if (sharedStream.node != null) {
						const streamSize = {
							height: sharedStream.node.videoHeight,
							width: sharedStream.node.videoWidth,
						};
						streamRef.update(streamSize);
					}
				}));
				// Save sharedStream
				cache.streams.shared[sharedStream.uid] = sharedStream;
				// Remove shared stream on Disconnect
				DataSync.onDisconnect(`_/rooms/${roomId}/streams/${sharedStream.uid}`).remove();
				// Remove shared stream on Disconnect
				DataSync.onDisconnect(`_/rooms/${roomId}/subscribers/${sharedStream.uid}`).remove();
				// Start listening to subscribers
				const
					path = `_/rooms/${sharedStream.roomId}/subscribers/${sharedStream.uid}`,
					value = snapData => Object.assign({device: snapData.name()}, snapData.val() || {});
				DataSync.on(path, 'child_added',
					snapData => {
						const subscriber = value(snapData);
						Log.d('Local~subscribed', subscriber);
						cache.peerConnections.offer(sharedStream, subscriber)
							.then(pc => sharedStream.peerConnections.push(pc));
					},
					Log.e.bind(Log)
				);
				DataSync.on(path, 'child_removed',
					snapData => {
						const subscriber = value(snapData);
						Log.d('Local~un-subscribed', subscriber);
						const closedPC = cache.peerConnections.close(sharedStream.uid, subscriber.device);
						sharedStream.peerConnections = sharedStream.peerConnections.filter(pc => pc !== closedPC);
					},
					Log.e.bind(Log)
				);
				Log.d('Local~shared', {sharedStream});
				return sharedStream;
			});
	}
}

import cache from '../util/cache';
import * as DataSync from '../util/DataSync';
import * as Log from '../util/Log';
import * as Events from '../../definitions/Events';

/**
 * A published Stream
 * @public
 */
export default class Remote {
	/**
	 * @access protected
	 * @param {object} values
	 */
	constructor(values) {
		Log.d('Remote~new', values);
		/**
		 * The uid of the room the stream is published in
		 * @type {string}
		 */
		this.roomId = values.roomId;
		/**
		 * The uid of this stream
		 * @type {string}
		 */
		this.uid = values.uid;
		/**
		 * The uid of the publisher of the stream
		 * @type {string}
		 */
		this.from = values.from;
		/**
		 * The type of the stream
		 * @type {string}
		 */
		this.type = values.type;
		/**
		 * @type {string}
		 */
		this.device = values.device;
		/**
		 * the user agent of the publisher of the stream
		 * @type {string}
		 */
		this.userAgent = values.userAgent;
		/**
		 * @type {string}
		 */
		this.height = values.height;
		/**
		 * @type {string}
		 */
		this.width = values.width;
		/**
		 * The local DOM container element where the {@link Local~media} is displayed
		 * @type {Element}
		 */
		this.container = cache.config.remoteStreamContainer;
		/**
		 * @type {{audio: boolean, video: boolean}}
		 */
		this.muted = Object.assign({audio: false, video: false}, values.muted);
		/**
		 * List of callbacks for mute status change
		 * @type {{MUTE: function[]}}
		 * @private
		 */
		this._callbacks = {};
		/**
		 * PeerConnections associated to this remote stream
		 * @type {PeerConnection}
		 */
		this.peerConnection = null;
	}

	/**
	 * DOM element where the MediaStream is displayed
	 * @returns {Element}
	 */
	get node() {
		return this.peerConnection ? this.peerConnection.node : null;
	}

	/**
	 * Subscribe to the stream
	 * @param {Element} [remoteStreamContainer] The element the stream is attached to. Can be null if already specified in ReachConfig.
	 * @returns {Promise}
	 */
	subscribe(remoteStreamContainer) {
		if(!cache.user) {
			return Promise.reject(new Error('Only an authenticated user can subscribe to a Room\'s stream.'));
		}
		// TODO: Test if not already subscribed ?
		this.container = remoteStreamContainer || cache.config.remoteStreamContainer;
		Log.d('Remote~subscribe', this.container);
		return cache.peerConnections.answer(this, this.container)
			.then(pc => {this.peerConnection = pc; })
			.then(() => DataSync.update(`_/rooms/${this.roomId}/subscribers/${this.uid}/${cache.device}`, {
				to: cache.user.uid,
				_created: DataSync.ts()
			}))
			.then(() => {
				DataSync.onDisconnect(`_/rooms/${this.roomId}/subscribers/${this.uid}/${cache.device}`).remove();
				let subscribed = false;
				DataSync.on(`_/rooms/${this.roomId}/streams/${this.uid}`, 'value', snapData => {
					const values = snapData.val();
					Log.d('Remote~updated', values);
					if(values) {
						// Update type
						this.type = values.type;
						//update stream size
						const height = values.height;
						const width = values.width;
						if((height || width) && (height !== this.height || width !== this.height)) {
							this.height = values.height;
							this.width = values.width;
							Log.w(this._callbacks[Events.stream.SIZE]);
							(this._callbacks[Events.stream.SIZE] || []).forEach(cb => cb(this.height, this.width));
						}
						// Update mute status
						const muted = values.muted;
						if(muted && (muted.audio !== this.muted.audio || muted.video !== this.muted.video)) {
							this.muted = muted;
							Log.w(this._callbacks[Events.stream.MUTE]);
							(this._callbacks[Events.stream.MUTE] || []).forEach(cb => cb(this.muted));
						}
						subscribed = true;
					} else if(subscribed) {
						Log.i('Remote#removed', this);
						this._close(true);
					}
				});
			})
			// .catch(Log.r('Remote~subscribe'));
			.catch((e) => {
				console.error(`PeerConnection~subscribe#error ${e}`);
				return Promise.reject(
					new Error('Failed to subscribe to the remote stream')
				);
			});
	}

	/**
	 * Unsubscribe from the stream
	 * @returns {Promise}
	 */
	unSubscribe() {
		return this._close(false);
	}

	/**
	 * Close the remote Stream
	 * @param {boolean} remote Close is initiated by publisher
	 * @returns {*}
	 * @private
	 */
	_close(remote) {
		// Cancel onDisconnect
		DataSync.onDisconnect(`_/rooms/${this.roomId}/subscribers/${this.uid}/${cache.device}`).cancel();
		// Stop listening to stream modifications
		DataSync.off(`_/rooms/${this.roomId}/streams/${this.uid}`, 'value');
		// Un-subscribe
		!remote && DataSync.remove(`_/rooms/${this.roomId}/subscribers/${this.uid}/${cache.device}`);
		// Close PeerConnection
		return Promise.resolve(cache.peerConnections.close(this.uid, this.device));
	}

	/**
	 * Register a callback for a specific event
	 * @param {string} event The event name ({@link Events/Stream})
	 * @param {function} callback The callback for the event
	 */
	on(event, callback) {
		if(Events.stream.supports(event)) {
			if(!this._callbacks[event]) {
				this._callbacks[event] = [];
			}
			this._callbacks[event].push(callback);
		}
	}

	/**
	 * Register a callback for a specific event
	 * @param {string} [event] The event name ({@link Events/Stream})
	 * @param {function} [callback] The callback for the event
	 */
	off(event, callback) {
		if(!event) {
			this._callbacks = {};
		} else if(Events.stream.supports(event)) {
			if(!callback) {
				this._callbacks[event] = [];
			} else {
				this._callbacks[event] = this._callbacks[event].filter(cb => cb !== callback);
			}
		}
	}

	/**
	 * @access protected
	 * @param {object} values
	 */
	update(values) {
		Object.keys(values).forEach(key => {this[key] = values[key];});
	}
}

import Remote from './Remote';

/**
 * @access protected
 */
export default class StreamManager {
	/**
	 * Constructor
	 * @access protected
	 */
	constructor() {
		/**
		 * Shared local streams
		 * @type {{*: Local}}
		 */
		this.shared = {};
		/**
		 * Subscribed remote streams
		 * @type {{*: Remote}}
		 */
		this.remote = {};
	}

	/**
	 * Get a Remote stream reference
	 * @param {object} streamData
	 * @returns {Remote}
	 */
	getRemote(streamData) {
		let remoteStream = this.remote[streamData.uid];
		if(remoteStream) {
			remoteStream.update(streamData);
		} else {
			this.remote[streamData.uid] = remoteStream = new Remote(streamData);
		}
		return remoteStream;
	}

	/**
	 * Get a Remote stream reference
	 * @param {object} streamData
	 * @returns {Remote}
	 */
	getShared(streamData) {
		// TODO: If it does not exists locally as it should, maybe we should remove it ?
		return this.shared[streamData.uid];
	}

}

import cache from './cache';

/**
 * Get the event string for Webcom from Reach events
 * @access protected
 * @param {string} event A Reach event
 * @return {string}
 */
export const eventType = (event) => {
	let evt;
	if((/_ADDED$/i).test(event) || /_PUBLISHED$/.test(event)) {
		evt = 'added';
	} else if(/_CHANGED$/.test(event) || /_REFRESHED$/.test(event)) {
		evt = 'changed';
	} else if(/_REMOVED$/.test(event) || /_UNPUBLISHED$/.test(event)) {
		evt = 'removed';
	}
	return evt ? `child_${evt}` : event;
};

/**
 * Write method
 * @param {string} method Write method (set,update)
 * @param {string} path The path to set
 * @param {object} data The data to set
 * @ignore
 */
const _write = (method, path, data) => new Promise((resolve, reject) => {
	cache.base.child(path)[method](data, error => error ? reject(error) : resolve());
});

/**
 * {@link Webcom#set} as a {@link Promise}
 * @access protected
 * @param {string} path The path to set
 * @param {object} data The data to set
 * @return {Promise<*, Error>}
 */
export const set = _write.bind(undefined, 'set');

/**
 * {@link Webcom#push} as a {@link Promise}
 * @access protected
 * @param {string} path The path to push
 * @param {object} data The data to push
 * @return {Promise<Webcom, Error>}
 */
export const push = (path, data) => new Promise((resolve, reject) => {
	const pushRef = cache.base.child(path).push(data, error => error ? reject(error) : resolve(pushRef));
});

/**
 * {@link Webcom#update} as a {@link Promise}
 * @access protected
 * @param {string} path The path to update
 * @param {object} data The data to update
 * @return {Promise<*, Error>}
 */
export const update = _write.bind(undefined, 'update');

/**
 * {@link Webcom#remove} as a {@link Promise}
 * @access protected
 * @param {string} path The path to remove
 * @return {Promise<*, Error>}
 */
export const remove = (path) => new Promise((resolve, reject) => {
	cache.base.child(path).remove(error => error ? reject(error) : resolve());
});

/**
 * {@link Webcom#once} shortcut as a {@link Promise}
 * @access protected
 * @param {string} path The path
 * @param {string} event The event
 * @returns {Promise<Webcom/api.DataSnapshot, Error>}
 */
export const once = (path, event) => new Promise((resolve, reject) => {
	cache.base.child(path).once(eventType(event), resolve, reject);
});

/**
 * {@link Webcom#once}('value') as a {@link Promise}
 * @access protected
 * @param {string} path The path to get
 * @return {Promise<Webcom/api.DataSnapshot, Error>}
 */
export const get = path => once(path, 'value');

/**
 * List values as a {@link Promise}
 * @access protected
 * @param {string} path The path to get
 * @param {function} Type The type of the object to list
 * @param params Additional constructor parameters
 * @return {Promise<Object[], Error>}
 */
export const list = (path, Type, ...params) => {
	return get(path)
	.then(snapData => {
		if(snapData) {
			const values = [];
			snapData.forEach(snapChild => {
				values.push(new Type(snapChild, ...params));
			});
			return values;
		}
	});
};

/**
 * {@link Webcom#on} shortcut
 * @access protected
 * @param {string} path The path
 * @param {string} event The event
 * @param {Webcom/api.Query~queryCallback|function} queryCallback The callback
 * @param {Webcom/api.Query~cancelCallback|function} [cancelCallback] The callback
 */
export const on = (path, event, queryCallback, cancelCallback) => {
	cache.base.child(path).on(eventType(event), queryCallback, cancelCallback);
};

/**
 * {@link Webcom#off} shortcut
 * @access protected
 * @param {string} path The path
 * @param {string} event The event
 * @param {function} [callback] The callback
 */
export const off = (path, event, callback) => {
	cache.base.child(path).off(eventType(event), callback);
};

/**
 * {@link Webcom#onDisconnect} shortcut
 * @access protected
 * @param {string} path The path
 * @return {Webcom/api.OnDisconnect}
 */
export const onDisconnect = path => cache.base.child(path).onDisconnect();

/**
 * Timestamp value
 * @access protected
 * @type {function}
 */
// export const ts = () => Webcom.ServerValue.TIMESTAMP;
export const ts = () => Date.now();
// HACK #DataSync: Rollback when TIMESTAMP works again (server-side bug with security rules)

import cache from './cache';

/**
 * List of possible log levels
 * @access private
 * @type {string[]}
 */
const levels = ['DEBUG', 'INFO', 'WARN', 'ERROR'];
/**
 * Is log enabled for a specific level
 * @access private
 * @param {string} level The level to test
 * @returns {boolean}
 */
const isEnabled = level => {
	return levels.indexOf(level.toUpperCase()) >= levels.indexOf(cache.logLevel.toUpperCase());
};
/**
 * Global Console. (to avoid drop_console...)
 * @access private
 * @type {Console}
 */
const gConsole = global['console'];
/**
 * Shorthand for console call
 * @access private
 * @param {string} method A method of the console object
 * @param {string} level The log level
 * @param {*} messages the log content
 */
const logger = (method, level, ...messages) => {
	isEnabled(level) ? gConsole[method.toLowerCase()].bind(gConsole)(...messages) : () => {};
};
/**
 * Use a group to log
 * @access private
 * @param {string} level The log level
 * @param {string} message The group title
 * @param {*} items the log content
 */
const group = (level, message, ...items) => {
	if (items.length > 0) {
		logger.bind(undefined, gConsole.group ? 'group' : level, level)(typeof message === 'string' ? message : '');
		let values = (typeof message !== 'string' ? [message] : []).concat(items);
		if (items.length === 1 && items[0] instanceof Array && typeof items[0].length !== undefined) {
			values = items[0];
		}
		values.forEach(item => logger.bind(undefined, level, level)(item));
		gConsole.groupEnd && gConsole.groupEnd();
	} else {
		logger.bind(undefined, level, level, message);
	}
};

/**
 * Log debug messages if log level is set to DEBUG.
 * @access protected
 * @see https://developer.mozilla.org/fr/docs/Web/API/Console/log
 */
export const d = group.bind(undefined, levels[0]);
/**
 * Log information messages if log level is set to DEBUG or INFO.
 * @access protected
 * @see https://developer.mozilla.org/fr/docs/Web/API/Console/info
 */
export const i = group.bind(undefined, levels[1]);
/**
 * Log warning messages if log level is set to DEBUG, INFO or WARN.
 * @access protected
 * @see https://developer.mozilla.org/fr/docs/Web/API/Console/warn
 */
export const w = group.bind(undefined, levels[2]);
/**
 * Log error messages. Errors are always logged.
 * @access protected
 * @see https://developer.mozilla.org/fr/docs/Web/API/Console/error
 */
export const e = gConsole.error.bind(gConsole);
/**
 * Promise rejection logger to use with catch
 * @access protected
 * @param {string} message
 * @returns {function}
 */
export const r = message => reason => {
	d(message, reason);
	return Promise.reject(reason || message);
};

