	/**
	 * Send SDP offer to the remote via DataSync
	 * @private
	 */
	_sendSdpToRemote() {
		// Log.d('PeerConnection~_sendSdpToRemote#localSDP', this.pc.localDescription.sdp);
		const remoteUserId = this.remote.to ? this.remote.to : this.remote.from;
		Device.get(remoteUserId, this.remote.device)
			.then((remoteDevice) => {
				const sdpOffer = this.pc.localDescription.sdp;
				let newSdp = sdpOffer;
				const local = /Chrome\/([0-9]+)/.exec(navigator.userAgent);
				const remote = /Chrome\/([0-9]+)/.exec(remoteDevice.userAgent);

				if (navigator.userAgent.indexOf('Chrome')!== -1 &&
					navigator.userAgent.indexOf('Android') !== -1 &&
					remoteDevice.userAgent.indexOf('Safari')!== -1 &&
					local[1] <= 64) {
					// newSdp =	this._addVP8Codec(sdpOffer);
					if (local[1] <= 60) {
						newSdp = newSdp.replace(/;profile-level-id=([a-z0-9]+)/,'');
					} else {
						newSdp =	newSdp.replace('42001f','42e01f');
					}
				}
				if (navigator.userAgent.indexOf('Safari')!== -1 &&
					remoteDevice.userAgent.indexOf('Chrome')!== -1 &&
					remoteDevice.userAgent.indexOf('Android')!== -1 &&
					remote[1] <= 64) {
					// newSdp =	this._addVP8Codec(sdpOffer);
					if (remote[1] <= 60) {
						newSdp = newSdp.replace(/;profile-level-id=([a-z0-9]+)/,'');
					} else {
						newSdp =	newSdp.replace('42e01f','42001f');
					}
				}
				Log.d(`PeerConnection~_sendSdpToRemote#SDP sent to remote ${newSdp}`);
				const descriptionChanged = {
					sdp: newSdp,
					type: this.pc.localDescription.type
				};
				DataSync.update(`${this._localPath}/sdp`, _toJSON(descriptionChanged));

			});
	}

	/**
	 * Create SDP offer and push it
	 * @returns {Promise}
	 * @private
	 */
	_sendOffer() {
		Log.d('PeerConnection~_sendOffer');
		return this.pc.createOffer()
			.then(description => this._setPreferredCodecs(description))
			.then(description => this.pc.setLocalDescription(description))
			.then(() => Log.d('PeerConnection~_sendOffer#localDescription', this.pc.localDescription.sdp))
			.then(() => this._sendSdpToRemote());
	}

	/**
	 * Add/Remove tracks to the PeerConnection stream
	 * @param {MediaStream} stream
	 * @param {string} method
	 * @private
	 */
	_alterStream(stream, method) {
		if(Object.getOwnPropertyDescriptor(RTCPeerConnection.prototype, `${method}Track`)) {
			if (method === 'add') {
				stream.getTracks().forEach(track => this.pc[`${method}Track`](track, stream), this);
			} else {
				this.pc.getSenders().forEach(sender => this.pc[`${method}Track`](sender), this);
			}
		} else {
			this.pc[`${method}Stream`](stream);
		}
	}

	/**
	 * Restart SDP negotiation following a MediaStream change
	 * @access protected
	 * @param {MediaStream} oldStream
	 * @param {MediaStream} newStream
	 */
	renegotiate(oldStream, newStream) {
		Log.d('PeerConnection~renegotiate');
		if((Object.getOwnPropertyDescriptor(RTCPeerConnection.prototype, 'getSenders'))
			&& ('RTCRtpSender' in window)
			&& Object.getOwnPropertyDescriptor(RTCRtpSender.prototype, 'replaceTrack')){

			// mozRTCPeerConnection implementation
			this.pc.getSenders().forEach(sender => {
				let newTracks;
				switch (sender.track.kind) {
					case 'audio':
						newTracks = newStream.getAudioTracks();
						break;
					case 'video':
						newTracks = newStream.getVideoTracks();
						break;
					default:
						newTracks = [];
				}
				if(newTracks.length) {
					sender.replaceTrack(newTracks[0]);
				}
			});
			this._sendOffer()
				.catch(e => {Log.d('PeerConnection~renegotiate', e);});
		} else {
			this._alterStream(oldStream, 'remove');
			this._alterStream(newStream, 'add');
		}
	}

	/**
	 * Close the PeerConnection and stop listening to SDP messages
	 * @access protected
	 */
	close() {
		if(this._status === OPENED) {
			this._status = CLOSING;
			// Stop display
			if (this.node) {
				this.node.stop && this.node.stop();
				this.node.srcObject = null;
				this.container.removeChild(this.node);
				this.node = null;
			}
			// Stop listening to remote ICE candidates
			this._remoteICECandidates(false);
			// Stop listening to SDP messages
			DataSync.off(`${this._remotePath}/sdp`, 'value');
			// Remove data
			DataSync.remove(this._localPath);
			// Close PeerConnection
			if (this.pc && this.pc.signalingState !== 'closed') {
				this.pc.onsignalingstatechange = () => {
					if(this.pc.signalingState !== 'closed') {
						this._status = CLOSED;
					}
				};
				this.pc.close();
			} else {
				this._status = CLOSED;
			}
		}
	}

	/**
	 * Edits the SDP to set the preferred audio/video codec
	 * @access private
	 * @param {RTCSessionDescription} description The description retrieved by createOffer/createAnswer
	 * @returns {RTCSessionDescription|{sdp: string, type: string}}
	 */
	_setPreferredCodecs(description) {
		if(cache.config.preferredVideoCodec || cache.config.preferredAudioCodec) {
			Log.d('PeerConnection~_setPreferredCodecs', {description, config: cache.config});
			const sdpLines = description.sdp.split(/\r?\n/);
			const medias = {audio: [], video: []};
			let current = null;
			// Parse SDP
			sdpLines.forEach((sdpLine, i) => {
				if(/^m=/.test(sdpLine)) {
					const d = /^m=(\w+)\s[0-9\/]+\s[A-Za-z0-9\/]+\s([0-9\s]+)/.exec(sdpLine);
					current = {
						fmt: d[2].split(/\s/),
						index: i,
						codecs: []
					};
					medias[d[1]].push(current);
				} else if(current && /^a=rtpmap:/.test(sdpLine)) {
					const c = /^a=rtpmap:(\d+)\s([a-zA-Z0-9\-\/]+)/.exec(sdpLine);
					if(c) {
						current.codecs.push({
							id: c[1],
							name: c[2],
							index: i
						});
					}
				}
			});
			Log.d('PeerConnection~_setPreferredCodecs', medias);
			let update = false;
			const prefer = (mediaList, preferedCodec) => {
				mediaList.forEach(media => {
					const selected = media.codecs.find(codec => preferedCodec.test(codec.name));
					if(selected) {
						const fmt = [selected.id].concat(media.fmt.filter(ids => ids !== selected.id));
						sdpLines[media.index] = sdpLines[media.index].replace(media.fmt.join(' '), fmt.join(' '));
						update = true;
					}
				});
			};
			if(cache.config.preferredVideoCodec) {
				prefer(medias.video, cache.config.preferredVideoCodec);
			}
			if(cache.config.preferredAudioCodec) {
				prefer(medias.audio, cache.config.preferredAudioCodec);
			}
			if(update) {
				Log.d('PeerConnection~_setPreferredCodecs', sdpLines.join('\r\n'));
				return {
					sdp: sdpLines.join('\r\n'),
					type: description.type
				};
			}
		}
		return description;
	}
}

import PeerConnection from './PeerConnection';
import cache from '../util/cache';
import * as Log from '../util/Log';
import * as DataSync from '../util/DataSync';

const getShortStackId = (id1, id2) => id1.localeCompare(id2, 'en-us') > 0 ? `${id1}-${id2}` :`${id2}-${id1}`;

const hashCode = (str) =>  {
	return str.split('').reduce((prevHash, currVal) =>
    (((prevHash << 5) - prevHash) + currVal.charCodeAt(0))|0, 0);
};

const getStackId = (deviceId1, deviceId2) => {
	const shortstackId = getShortStackId(deviceId1, deviceId2);

	const hash = `${hashCode(shortstackId)}`;
	const length = hash.length;
	const id1 = hash.substring(length-3,length);
	const id2 = hash.substring(length-6,length-3);

	return `${id1}/${id2}/${shortstackId}`;
};

/**
 * @access protected
 */
export default class PeerConnectionManager {
	/**
	 * Constructor
	 * @access protected
	 */
	constructor() {
		/**
		 * WebRTC stacks
		 * @type {{*: {*: PeerConnection}}}
		 */
		this.stacks = {};
	}

	/**
	 * Get a PeerConnection object for a specific stream
	 * @param {Local|Remote} stream
	 * @param {Remote|{to: string, device:string}} remote
	 * @param {boolean} publish
	 * @return {*}
	 */
	getPeerConnection(stream, remote, publish) {
		if(!cache.user) {
			return Promise.reject(new Error('Only an authenticated user can get a PeerConnection\'s object.'));
		}
		const stackId = getStackId(remote.device, cache.device);

		if(this.stacks[stackId] && this.stacks[stackId][stream.uid]) {
			return Promise.resolve(this.stacks[stackId][stream.uid]);
		}

		if(!this.stacks[stackId]) {
			this.stacks[stackId] = {};
		}

		const userId = cache.user.uid.split('/');
		const shortUserId = userId[2];
		let shortRemoteTo = undefined;
		let shortRemoteFrom = undefined;
		if (remote.to) {
			const remoteTo = remote.to.split('/');
			shortRemoteTo = remoteTo[2];
		} else {
			const remoteFrom = remote.from.split('/');
			shortRemoteFrom = remoteFrom[2];
		}

		const users = {};
		// users[cache.user.uid] = true;
		users[shortUserId] = true;
		// users[remote.from || remote.to] = true;
		users[shortRemoteFrom || shortRemoteTo] = true;

		return DataSync.update(`_/webrtc/${stackId}`, users)
			.then(() => {
				return new PeerConnection(stackId, stream.uid, remote, publish);
			})
			.then(pc => {
				Log.d('PeerConnectionManager~getPeerConnection', {stackId, streamId: stream.uid, pc});
				this.stacks[stackId][stream.uid] = pc;
				return pc;
			})
			.catch(Log.r('PeerConnectionManager~getPeerConnection'));
	}

	/**
	 * Create offer for a stream to a subscriber
	 * @param {Local} localStream
	 * @param {object} subscriber
	 * @return {Promise.<PeerConnection>}
	 */
	offer(localStream, subscriber) {
		Log.d('PeerConnectionManager~offer', {localStream, subscriber});
		return this.getPeerConnection(localStream, subscriber, true)
			.then(pc => pc.offer(localStream.media));
	}

	/**
	 * Answer to the offer from the publisher
	 * @param {Remote} remoteStream
	 * @param {Element} htmlElement
	 * @return {*|Promise.<PeerConnection>}
	 */
	answer(remoteStream, htmlElement) {
		Log.d('PeerConnectionManager~answer', {remoteStream, htmlElement});
		return this.getPeerConnection(remoteStream, remoteStream, false)
			.then(pc => pc.answer(htmlElement));
	}

	/**
	 * Close a PeerConnection
	 * @param streamId
	 * @param remoteDevice
	 * @return {*}
	 */
	close(streamId, remoteDevice) {
		const stackId = getStackId(remoteDevice, cache.device),
			pc = this.stacks[stackId] ? this.stacks[stackId][streamId] : null;
		if(pc){
			pc.close();
			DataSync.remove(`_/webrtc/${stackId}`);
			this.stacks[stackId][streamId] = null;
			delete this.stacks[stackId][streamId];
			return pc;
		}
		return false;
	}
}

import {browserDetails, browserShim} from 'webrtc-adapter';
import Webcom from 'webcom/webcom';

/**
 * The current browser's infos
 * @typedef {Object} Browser
 * @property {string} name - current browser's name
 * @property {number} [version] - current browser's version
 * @property {number} [minVersion] - current browser's minimum supported version
 * @property {boolean} compatible - is current browser supported ?
 */
export const browser = Object.assign({
	compatible: !/\s/.test(browserDetails.browser) &&
		(!browserDetails.minVersion || browserDetails.version >= browserDetails.minVersion)
}, browserDetails);

const version = `Reach v${SDK_VERSION} | Schema ${SCHEMA_VERSION} | Webcom v${Webcom.SDK_VERSION}`;
let message = '';
if(browser.version === null) {
	message = 'Unsupported browser: browser must support WebRTC Peer-to-peer connections to use Reach';
} else if (browser.minVersion && browser.minVersion > browser.version) {
	message = `Unsupported browser: browser is outdated, update to latest version (${browser.minVersion}+)`;
} else if(!browserShim){
	message = 'No shim for your browser. There might a problem with your package.';
}

// Log browser status & SDKs versions on load
let args = [`${version} ${message}`];
if(/^(chrome|firefox)$/.test(browser.browser)) {
	args = [
		`%c ${version} %c ${message}`,
		`background: ${browser.compatible ? '#f50' : 'red'}; color: #fff; font-weight: bold`,
		`color: ${browser.compatible ? 'inherit' : 'red'}`
	];
}
console.log(...args);

import User from '../core/User';
import Room from '../core/Room';
import Invite from '../core/Invite';
import Participant from '../core/Participant';
import Message from '../core/Message';
import Remote from '../core/stream/Remote';
import {ACCEPTED, REJECTED, CANCELED} from '../core/util/constants';

/**
 * The events supported by {@link Reach#on}
 * @typedef {Object} Events/Reach
 * @property {string} USER_ADDED Fired when a new user is registered
 * @property {string} USER_CHANGED Fired when an existing user logs in or out or changes is status
 * @property {string} USER_REMOVED Fired when a user is unregistered
 * @property {string} ROOM_ADDED Fired when a room is created
 * @property {string} ROOM_CHANGED Fired when a room status is changing
 * @property {string} ROOM_REMOVED Fired when a room is closed definitely
 * @property {string} INVITE_ADDED Fired when an invite is received
 * @property {string} INVITE_CHANGED Fired when an invite status is modified
 */

/**
 * The events supported by {@link Room#on}
 * @typedef {Object} Events/Room
 * @property {string} PARTICIPANT_ADDED Fired when a new participant is added to the room. Does not mean he's connected but that he's invited to
 * @property {string} PARTICIPANT_CHANGED Fired when a participant changes is status (enter/leaves the room)
 * @property {string} PARTICIPANT_REMOVED Fired when a user leaves definitely or is banned
 * @property {string} MESSAGE_ADDED Fired when a new instant message is sent to the room
 * @property {string} MESSAGE_CHANGED Fired when an instant message is edited
 * @property {string} MESSAGE_REMOVED Fired when an instant message is removed
 * @property {string} STREAM_PUBLISHED Fired when a participant publishes a stream
 * @property {string} STREAM_UNPUBLISHED Fired when a participant stops the publishing of his stream
 */

/**
 * The events supported by {@link Invite#on}
 * @typedef {Object} Events/Invite
 * @property {string} ACCEPTED Fired when the invite has been accepted
 * @property {string} REJECTED Fired when the invite has been accepted
 * @property {string} CANCELED Fired when the invite has been canceled
 */

/**
 * The events supported by {@link Remote#on}.
 * @typedef {Object} Events/Stream
 * @property {string} MUTE Fired when the mute status of the stream changes
 * @property {string} SIZE Fired when the size of the stream changes
 */

/**
 * Path configuration for each event
 * @ignore
 * @type {{}}
 */
const paths = {
	'USER': () => 'users',
	'ROOM': () => 'rooms',
	'INVITE': user => {
		if(!user) {
			throw new Error('You must be authenticated to list the invites');
		}
		return `_/invites/${user.uid}`;
	},
	'PARTICIPANT': room => `_/rooms/${room.uid}/participants`,
	'MESSAGE': room => `_/rooms/${room.uid}/messages`,
	'STREAM': room => `_/rooms/${room.uid}/streams`
};

/**
 * Object type configuration for each event
 * @ignore
 * @type {{}}
 */
const classes = {
	'USER': User,
	'ROOM': Room,
	'INVITE': Invite,
	'PARTICIPANT': Participant,
	'MESSAGE': Message,
	'STREAM': Remote
};

/**
 * Private class to add support test methods
 * @class _Events
 * @ignore
 */
class _Events {
	constructor(keys) {
		keys.forEach(key => {this[key] = key;});
	}
	supports(event) {
		if(!event || typeof event !== 'string' || this[event] !== event.toUpperCase()) {
			throw new Error(`Unsupported event. Use one of the following: ${JSON.stringify(Object.keys(this))}`);
		}
		return true;
	}
	toPath(event) {
		if(this.supports(event)) {
			return paths[event.toUpperCase().replace(/_.*$/, '')] || (() => null);
		}
	}
	toClass(event) {
		if(this.supports(event)) {
			return classes[event.toUpperCase().replace(/_.*$/, '')];
		}
	}
}

/**
 * @ignore
 */
export const reach = new _Events([
	'USER_ADDED', 'USER_CHANGED', 'USER_REMOVED',
	'ROOM_ADDED', 'ROOM_CHANGED', 'ROOM_REMOVED',
	'INVITE_ADDED', 'INVITE_CHANGED'
]);

/**
 * @ignore
 */
export const room = new _Events([
	'MESSAGE_ADDED', 'MESSAGE_CHANGED', 'MESSAGE_REMOVED',
	'PARTICIPANT_ADDED', 'PARTICIPANT_CHANGED', 'PARTICIPANT_REMOVED',
	'STREAM_PUBLISHED', 'STREAM_UNPUBLISHED'
]);

/**
 * @ignore
 */
export const stream = new _Events(['MUTE', 'SIZE']);

/**
 * @ignore
 */
export const invite = new _Events([ACCEPTED, REJECTED, CANCELED]);

/**
 * The available stream types
 * @typedef {Object} StreamTypes
 * @property {string} AUDIO='audio' - Audio communication service
 * @property {string} VIDEO='video' - Video communication service
 * @property {string} AUDIO_VIDEO='audio-video' - Audio and video communication service
 * @property {string} SCREEN_SHARING='screen-sharing' - Screen-sharing communication service
 */

/**
* Audio communication service
* @ignore
* @type {string}
*/
export const AUDIO = 'audio';
/**
* Video communication service
* @ignore
* @type {string}
*/
export const VIDEO = 'video';
/**
* Audio and video communication service
* @ignore
* @type {string}
*/
export const AUDIO_VIDEO = 'audio-video';
/**
* Screen-sharing service
* @ignore
* @type {string}
*/
export const SCREEN_SHARING = 'screen-sharing';

export default {AUDIO, VIDEO, AUDIO_VIDEO, SCREEN_SHARING};

